<div class="modal1" id="RegistrationModal">
        <div class="modal1-box">
            <span class="close1" id="closeModal1">&times;</span>
            <form action="/THREADX/dependencies/_handles_registration.php" method="post" id="RegistrationForm">
                <h2>Registration</h2>
                    <label for="registerUsername">Username:</label>
                    <input type="text" id="registerUsername" name="registerUsername" required>
                    <label for="registerEmail">Email ID:</label>
                    <input type="email" id="registerEmail" name="registerEmail" required>
                    <label for="registerPassword">Password:</label>
                    <input type="password" id="registerPassword" name="registerPassword" required>
                    <label for="registerCpassword">Confirm Password:</label>
                    <input type="password" id="registerCpassword" name="registerCpassword" required>
                    <button type="submit">Create New Account</button>
                </form>
            </form>
        </div>
    </div>